import React, { component } from 'react';
// import { useState, useEffect } from 'react';
import { BarChart } from 'reaviz';

class Barchart extends React.Component {
  constructor() {
    this.state = {
      barData: []
    }
    this.calculateOccurrence = this.calculateOccurrence.bind(this);
  }

  componentDidMount() {
      fetch("http://localhost:8080/api/feedback", {
        method: "GET"
      })
      .then(res => res.json())
      .then(data => this.setState({barData: calculateOccurrence(data)}));
        // setBarData(calculateOccurrence(data).map(([key, data]) => ({key, data})))
  }

  calculateOccurrence(data) { 
      let wordFreq = [];
      let allWords = {};
      for (let i=0; i < data.length; i++) {
        var words = data[i].user_query.split(" ")
        for (let j=0; j < words.length; j++) {
          if (!(words[j] in allWords)) {
            allWords[words[j]] = 1
          } else {
            allWords[words[j]] += 1
          }
        }
      }
      for (var word in allWords) {
        wordFreq.push([word, allWords[word]])
      }
      wordFreq = wordFreq.sort((a, b) => b[1] - a[1]);
      var wordFrequencyData = wordFreq.map(([key, data]) => ({key, data}));
      console.log(wordFrequencyData);
      return wordFrequencyData;
  }

  render() {
    const { barData } = this.state;

    <div>
      <BarChart 
      data={barData}
      // data={[
      //   {key: "A", data: 1},
      //   {key: "B", data: 2}
      // ]}
      height={300}
      width={300}
      />
      Barchart here! 
    </div>
  }
}

export default Barchart;

