import { Component } from "react";
import * as moment from 'moment';

class Metric extends Component {
  constructor(props) {
    super(props);
    this.state = {
      count: 0,
      time: ""
    };
  }

  async componentDidMount() {
    let response = await fetch("http://localhost:8080/api/feedback", {
      method: "GET",
    });

    let data = await response.json();
    let times = data.map((x) => x.createdAt)
    let latest = times[0]
    for (let i=0; i < times.length; i++) {
        if (times[i] > latest) {
            latest = times[i]
        }
    }
    console.log(latest)
    latest = latest.substring(0,10) + ", " + latest.substring(11, 19)

    const formatTime = (createdAt) => {
        const thisMoment = moment.utc(createdAt).format("h:mm");
        return thisMoment;
     };

    this.setState({
        count: data.length,
        time: formatTime(latest) // last updated
    })
  };

  render() {
    const { count, time } = this.state;

    return (
    <div>
      <div>
          <h2>
            Number of Queries Asked: 
          </h2>
            <h3>{count}</h3>
            </div>
            <br/>
            <h2>
              Last Asked Query: 
            </h2><strong>{time}</strong>
      </div>
    );
  }
}

export default Metric;
