import { Component } from "react";
import * as moment from "moment";

class Metric extends Component {
  constructor(props) {
    super(props);
    this.state = {
      count: 0,
      time: "",
    };
  }

  async componentDidMount() {
    let response = await fetch("http://localhost:8080/api/feedback", {
      method: "GET",
    });

    let data = await response.json();
    let times = data.map((x) => x.createdAt);
    let latest = times[0];
    for (let i = 0; i < times.length; i++) {
      if (times[i] > latest) {
        latest = times[i];
      }
    }
    latest = new Date(latest);
    this.setState({
      count: data.length,
      time: moment(latest)._i.toLocaleString(),
    });
  }

  render() {
    const { count, time } = this.state;

    return (
      <div className="row">
        <h3>Total Number of Queries Asked</h3>
        <h1>{count}</h1>
        <br />
        <h3>Time of Last Asked Query</h3>
        <h1>{time}</h1>
      </div>
    );
  }
}

export default Metric;
