import { useState, useEffect } from 'react';
import { BarChart } from 'reaviz';

export default function Barchart() {
    const [barData, setBarData] = useState([]);

    // const uploadData = new FormData(data.target);
    // const backendUrl = "http://localhost:8080"; // change to whatever
    // axios.get(`${backendUrl}/api/feedback`)
    //   .then((res) => {
    //     setSubmitted(res);
    //   })
    //   .catch((error) => {
    //     setError(error);
    //     console.log(error)
    //   });
    function calculateOccurrence(data) {
      let wordFrequencyData = [];
      let allWords = {};
      for (let i=0; i < data.length; i++) {
        var words = data[i].user_query.split(" ")
        for (let j=0; j < words.length; j++) {
          if (!(words[j] in allWords)) {
            allWords[words[j]] = 1
          } else {
            allWords[words[j]] += 1
          }
        }
      }
      console.log(allWords);
      for (var word in allWords) {
        wordFrequencyData.push([word, allWords[word]])
      }
      wordFrequencyData = wordFrequencyData.sort((a, b) => b[1] - a[1])
      return wordFrequencyData
    };

    useEffect(() => {
        fetch("http://localhost:8080/api/feedback", {
            method: "GET"            
        })
        .then(res => res.json())
        .then(data => {
          setBarData(calculateOccurrence(data)); 
          console.log(barData) 
        })
    }, [])

    return (
      <div>
        <BarChart 
        // data={barData}
        data={[
          {key: "A", data: 1},
          {key: "B", data: 2}
        ]}
        height={300}
        width={300}
        />
        Barchart here! 
      </div>
    )
}
