import { Component } from "react";

class Metric extends Component {
  constructor(props) {
    super(props);
    this.state = {
      count: 0,
      time: ""
    };
  }

  async componentDidMount() {
    let response = await fetch("http://localhost:8080/api/feedback", {
      method: "GET",
    });

    let data = await response.json();
    console.log(data)
    // let latest = data.
    this.setState({
        count: data.length
        // time: data. // last updated
    })
  };

  render() {
    const { count, time } = this.state;

    return (
      <div>
          Number of Queries Asked: 
          <h1>{count}</h1>

          Last Asked Query: 
            <h1>{time}</h1>
      </div>
    );
  }
}

export default Metric;
