import { Component } from "react";
import {
  BarChart,
  CartesianGrid,
  XAxis,
  YAxis,
  Tooltip,
  Bar,
  Legend,
} from "recharts";

class Barchart extends Component {
  constructor(props) {
    super(props);
    // this.loadData = this.loadData.bind(this);
    this.state = {
      barData: [],
    };
  }

  async componentDidMount() {
    try {
      let response = await fetch("http://localhost:8080/api/feedback", {
        method: "GET",
      });

      let data = await response.json();
      let wordFreq = [];
      let allWords = {};
      for (let i = 0; i < data.length; i++) {
        var words = data[i].user_query.split(" ");
        for (let j = 0; j < words.length; j++) {
          let word = words[j].toLowerCase();
          if (!(word in allWords)) {
            allWords[word] = 1;
          } else {
            allWords[word] += 1;
          }
        }
      }
      for (var word in allWords) {
        wordFreq.push([word, allWords[word]]);
      }
      wordFreq = wordFreq.sort((a, b) => b[1] - a[1]);
      wordFreq = wordFreq.map(([word, count]) => ({ word, count }));
      await this.setState({
        barData: wordFreq,
        loaded: true,
      });
      console.log(this.state.barData);
    } catch(err) {
      console.log(err)
    }
  };

  render() {
    const { barData } = this.state;

    return (
      <div>
        <BarChart
          data={barData}
          height={300}
          width={1000}
          // margin={{
          //   top: 5,
          //   right: 30,
          //   left: 20,
          //   bottom: 5,
          // }}
        >
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="word" />
          <YAxis />
          <Tooltip />
          <Legend />
          <Bar dataKey="word" fill="#8884d8" />
          <Bar dataKey="count" fill="#8884d8" />
        </BarChart>
      </div>
    );
  }
}

export default Barchart;
