import { Component } from 'react';
import ReactWordcloud from 'react-wordcloud';

class Wordcloud extends Component {
    constructor(props) {
      super(props);
      // this.loadData = this.loadData.bind(this);
      this.state = {
        wordData: [],
      };
    }

    async componentDidMount() {
      try {
        let response = await fetch("http://localhost:8080/api/feedback", {
          method: "GET",
        });

        let data = await response.json();
        let wordFreq = [];
        let allWords = {};
        console.log(data)
        for (let i = 0; i < data.length; i++) {
            if (data[i].feedback) {
                var words = data[i].feedback.split(" ");
                for (let j = 0; j < words.length; j++) {
                    let word = words[j].toLowerCase();
                    if ((word.match(/[a-z]/i))) {
                        if(!(word in allWords)) {
                            allWords[word] = 1;
                        } else {
                            allWords[word] += 1;
                        }
                    }
                }
            }
        }
        for (var word in allWords) {
          wordFreq.push([word, allWords[word]]);
        }
        wordFreq = wordFreq.sort((a, b) => b[1] - a[1]);
        wordFreq = wordFreq.map(([word, count]) => ({ word, count }));
        await this.setState({
          wordData: wordFreq,
        });
        console.log(this.state.wordData);
      } catch(err) {
        console.log(err)
      }
    };

    render() {
      const { wordData } = this.state;

      return (
        <div>
            <ReactWordcloud words={wordData} />
        </div>
      );
    }
  }

  export default Wordcloud;
